# csv order: .id, .weaknesses[0].type, .weaknesses[0].value
curl -X GET 'https://api.pokemontcg.io/v2/cards?q=id:base1%20supertype:pokemon' -H 'apikey: afa308c8-8082-4e19-871f-02c33fc09986' | jq '.data | .[] | [.id, .weaknesses[0].type, .weaknesses[0].value] | @csv' | tr -d '"\\' | awk -F"," 'BEGIN {OFS=","}; $3!=""'  | awk -F, 'BEGIN{ OFS=FS } {$1="\""$1"\""; $2="\""$2"\""; $3="\""$3"\""; print }' > weakness.csv
